import './Steps.css';

function Steps() {
    return (
    <div className="steps">
       <h2>Preparación</h2>
       <ul className="stepsList">
           <li><span>Ponga el LILLET BLANC y los cubitos de hielo en una copa de vino</span></li>
           <li><span>Agregue el Schweppes Russian Wild Berry</span></li>
           <li><span>Corta las fresas en cuartos y agrégalas a la bebida</span></li>
           <li><span>Revuelve suavemente la bebida y decora el vaso.</span></li>
       </ul>

    </div>
    )
}

export default Steps;
