import './Steps.css';

function Steps() {
    return (
    <div className="steps">
       <h2>Preparación</h2>
       <ul className="stepsList">
           <li><span>Ponga el LILLET BLANC y los cubitos de hielo en una copa de vino</span></li>
           <li><span></span>Agregue el Schweppes Russian Wild Berry</li>
           <li>Corta las fresas en cuartos y agrégalas a la bebida</li>
           <li>Revuelve suavemente la bebida y decora el vaso.</li>
       </ul>

    </div>
    )
}

export default Steps;
