import './IngredientsList.css';

function IngredientsList() {
    return (
    <div className="ingredients">
       <h2>Ingredientes</h2>
       <div className="ingredientsList">
         <ul>
           <li>Lillet Blanc o Lillet Rosè (5 cl)</li>
           <li>Schweppes Russian Wild Berry (10 cl)</li>
           <li>Frambuesa o Arándano (2 piezas)</li>
           <li>Fresa (1 pieza)</li>
           <li>Hierbabuena (1 ramita)</li>
           <li>Hielo</li>
       </ul>

       </div>


    </div>
    )
}

export default IngredientsList;
